function axisyy(axis_limits)%  AXISYY:  AXISYY is written to be used by PLOTYY to%  change the axes scaling.  This is similar to AXIS;%  however, it allows you to change both y-axes.%%  AXISYY([XMIN XMAX Y1MIN Y1MAX]):  This format uses%  AXIS to change the X and Y1 axes.%%  AXISYY([XMIN XMAX Y1MIN Y1MAX Y2MIN Y2MAX]): This%  format will change the scaling on all the axes.%  Written By:  John L. Galenski%  Date: 02/12/93N = length(axis_limits);if N < 4 | N == 5 | N > 6	error('Input must be 4 or 6 elements long')endhaxes = sort(get(gcf,'child'));ha1 = haxes(1);ha2 = haxes(2);ha3 = haxes(3);ha4 = haxes(4);ha5 = haxes(5);xmin = axis_limits(1);xmax = axis_limits(2);y1min = axis_limits(3);y1max = axis_limits(4);axis([xmin xmax y1min y1max])if N == 6    y2min = axis_limits(5);    y2max = axis_limits(6);    set(gcf,'currentaxes',ha2)    axis([xmin xmax y2min y2max])    for A = 2:5	eval(['set(gcf,''currentaxes'',ha',int2str(A),')'])	if A == 3	     set(gca,'yticklabelmode','auto')	end	axis([xmin xmax y2min y2max])    endend	set(gcf,'currentaxes',ha1)