function oscope(x,y,xr,yr)%Function OSCOPE(x,y,xr,yr) -- Oscilloscope plot%   USAGE: oscope(x,y,xrange,[ymin ymax])%%   Ex: oscope(1,rand,10,[0 1])%%   Try it out with this example,%   for i = 1:2000;oscope(i,rand,20,[0 1]);endm = get(gca,'userdata');cxr = get(gca,'xlim');if (m ~= 1.2),     set(gca,'xlim',[x(1) rem(x(1),xr)*xr+xr],'ylim',yr);     line(x,y,'linesty','-','erasemode','none');     set(gca,'userdata',1.2);     set(gca,'drawmode','fast','nextplot','add');elseif (x(1) > cxr(2))     pause	 set(gca,'xlim',[cxr(2) cxr(2)+xr]);     h = get(gca,'ch');     xd = get(h,'xdata');yd = get(h,'ydata');     set(h,'xdata',[xd(length(xd)) x],'ydata',[yd(length(yd)) y])elseif (x(1) < cxr(1))     clg; oscope(x,y,xr,yr);else     h = get(gca,'ch');     set(h,'xdata',[get(h(1),'xdata'),x],'ydata',[get(h(1),'ydata'),y])     drawnow;end