function stats1(dat)%Function STATS1(data)%  Compute and display [Min,Max,Mean,Median] of columns of <data>[nr,nc] = size(dat); %nc = 1:nc;disp(['    Column      Min       Max       Mean    Median'])disp(['    ======    ======    ======    ======    ======'])for k = 1:nc	disp(sprintf('%8i%12.4f%10.4f%10.4f%10.4f',...	[k,min(dat(:,k))',max(dat(:,k))',meanmiss(dat(:,k))',medianmiss(dat(:,k))']))end